package JavaBasicByNaveen;

public class IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 20;
		int c = 30;
		if(a>b & a>c) {
			System.out.println("A is greatest");
		}else if(b>c) {
			System.out.println("B is greatest");
		} else {
			System.out.println("C is greatest");
		}
	}

}
